async function selectionDialog(promptText,selection)
{
	//Will be replaced by HTML or something later
	console.assert(selection.length)
	console.assert(arguments.length===2,'Wrong number of arguments')
	let out=prompt(promptText+'\nPlease choose from these options (case sensitive):\n'+selection)
	if(selection.includes(out))
		return out
	if(!out)
		return undefined
	return selectionDialog(promptText,selection)
}

async function conditionedInputDialog(promptText,instructionText,condition,conditionFailMessage="Bad input, please try again!",cancelMessage="Cancelled input.")
{
	//TODO rewrite selectionDialog to use this function. this is copypasta trash.
	//condition is a boolean function that takes a user input string
	//An input that has to match a condition
	//Will be replaced by HTML or something later
	console.assert(arguments.length>=3,'Wrong number of arguments')
	let out=prompt(promptText+'\n'+instructionText)
	if(!out)
	{
		alert(cancelMessage)
		return undefined//user cancelled
	}
	if(condition(out))
	{
		return out
	}
	else
	{
		alert(conditionFailMessage)
		return conditionedInputDialog(promptText,instructionText,condition,conditionFailMessage,cancelMessage)
	}
}

window.numericInputDialog=async function (message, min=-1/0, max=1/0)//In javascript, 1/0 is positive infinity. -1/0 is negative infinity. Screw calculus, this is JS! (Sigh. At least I found a use for it?!)
{
	function condition(input)
	{
		try
		{
			input=JSON.parse(input)
			return typeof input==='number' && input<=max && input>=min
		}
		catch(e)
		{
			return false
		}
	}
	return Number(await (conditionedInputDialog(message,"Please enter any number between "+min+" and "+max,condition)))
}

